import React, { Component } from "react";
import ReactDOM from "react-dom";

import ThoughtData from "./ThoughtData";
import Loading from "./Loading";

class ShowAlert extends Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      isThoughtDisplayed: false,
      loading: true,
      errMsg: null
    }
  }

  handleClick() {
    this.setState({isThoughtDisplayed: true});
    this.setState(prevState => ({loading: !prevState.loading}));
  }

  // showAlert() {
  //   alert("sup");
  // }
  // displayThought = () => {
  //   this.setState(prevState => ({
  //     displayThought: !prevState.displayThought
  //   }));
  // }
  // componentDidMount() {
  //   this.displayThought();
  // }

  render() {
    const isThoughtDisplayed = this.state.isThoughtDisplayed;
    let button;

    button = <button onClick={this.handleClick}>Display Thought</button>

    return (
      <div>
        <Thought isThoughtDisplayed={isThoughtDisplayed}/>
        {button}
      </div>
    );
    // return <button onClick={this.displayThought}>show alert</button>;
  }
}

function Thought(state) {
   if (state.isThoughtDisplayed) {
     return <ThoughtData loading={this.props.loading}/> ;
   }
   return null;
 }

export default ShowAlert;

