import React, { Component } from "react";
import ReactDOM from "react-dom";

import ThoughtData from "./ThoughtData";

class ShowAlert extends Component {
  constructor() {
    super();
    this.state = {
      displayThought: true,
      loading: true,
      errMsg: null
    }
  }

  // showAlert() {
  //   alert("sup");
  // }
  displayThought = () => {
    this.setState(prevState => ({
      displayThought: !prevState.displayThought
    }));
  }
  componentDidMount() {
    this.displayThought();
  }
  render() {
    return <button onClick={this.displayThought}>show alert</button>;
  }
}

export default ShowAlert;

const rootElement = document.getElementById("root");
ReactDOM.render(<ShowAlert displayThought={true}/>, rootElement);
