import React, { Component } from 'react';
import axios from "axios";

export default class ThoughtData extends Component {
  constructor() {
    super();
    this.state = {
      thoughts: [],
      loading: true,
      errMsg: null
    }
  }

  getThoughts() {
    axios.get("http://localhost:8080/thought")
    .then(response => this.setState({
      thoughts: response.data,
      loading: false,
      errMsg: null
    }))
    .catch (err => this.setState({
      loading: false,
      errMsg: "data unavailable"
    }))
  }

  componentDidMount() {
    this.getThoughts();
  }

  render() {
    if(this.state.loading) {
      return( <div>Loading...</div>);
    }
    return (
      <div>
        <h1 className="employeeName">{this.state.thoughts?this.state.thoughts.name:<div></div>}</h1>
        <h2 id="currentBeerType">{this.state.thoughts?this.state.thoughts.currentBeer:<div></div>}</h2>
        <h2 id="currentThoughtQuote">{this.state.thoughts?this.state.thoughts.currentThought:<div></div>}</h2>
        <img id="daydreamImg" src={this.state.thoughts?this.state.thoughts.daydream:<div></div>} alt={"daydream"} />
        <img id="employeeImg" src={require("./img/empPics/"+this.state.thoughts.name+".png")} alt={"employeePicture"} />
      </div>
    );
  }
}
