      this.setState({ requirementKey: requirementKey + 1 })}>Display Thought</button>
    const{requirementKey}=this.state;

    return (
      <div>
        <Thought isThoughtDisplayed={isThoughtDisplayed}/>
        {button}
      </div>
    );
    // return <button onClick={this.displayThought}>show alert</button>;
  }
}

