import React, { Component } from "react";
import axios from "axios";

import ThoughtData from "./ThoughtData";

class DisplayThoughtButton extends Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      isThoughtDisplayed: false,
      loading: true,
      errMsg: null
    }
  }

  handleClick() {
    this.setState({isThoughtDisplayed: true});
  }

  render() {
    const isThoughtDisplayed = this.state.isThoughtDisplayed;
    let button;
    button = <button onClick={this.handleClick}>Display Thought</button>

    if (isThoughtDisplayed) {
      return (
        <div className="thoughtContainer">
          <Thought isThoughtDisplayed={this.state} />
          {button}
        </div>
      );
    } else {
      return (
        <div className="thoughtContainer">
          {button}
        </div>
      );
    }

  }
}

function GetThoughts() {
  let thoughts = [];
  axios.get("http://localhost:8080/thought")
  .then(response =>
    {
      thoughts = response.data;
      return true;
    }
  )
  .catch (err => console.log("ERROR"))
}

function Thought(state) {
  let thoughts = GetThoughts();

  if (state.isThoughtDisplayed) {
     return <ThoughtData thoughts={thoughts}/> ;
  }
  return null;
 }

export default DisplayThoughtButton;
