import React, { Component } from 'react';
import axios from "axios";

export default class ThoughtData extends Component {
  constructor() {
    super();
    this.state = {
      thoughts: [],
      buttonClicked: true,
      loading: true,
      errMsg: null
    }
  }



  componentDidMount(){

  }

  render() {

    return (
      <div className="fullThought">
        <div className="empNameAndImg">
          <img className="employeeImg" src={require("./img/empPics/"+this.state.thoughts.name+".png")} alt={"employeePicture"} />
          <h1 className="employeeName">{this.state.thoughts?this.state.thoughts.name:<div></div>}</h1>
        </div>
        <h2 className="currentBeerType">Current Beer: {this.state.thoughts?this.state.thoughts.currentBeer:<div></div>}</h2>
        <h2 className="currentThoughtQuote">Current Thought: {this.state.thoughts?this.state.thoughts.currentThought:<div></div>}</h2>
        <div className="daydream">
          <h2>Daydream</h2>
          <img src={this.state.thoughts?this.state.thoughts.daydream:<div></div>} alt={"daydream"} />
        </div>
      </div>
    );
  }
}
