import React, { Component } from "react";
import axios from "axios";

import ThoughtData from "./ThoughtData";

class DisplayThoughtButton extends Component {
  constructor() {
    super();
    this.state = {
      thoughts: [],
      isThoughtDisplayed: false,
      loading: true,
      errMsg: null
    }
  }

  getThoughts() {
    axios.get("http://localhost:8080/thought")
    .then(response =>
      {
        this.setState({
          thoughts: response.data,
          isThoughtDisplayed: true,
          loading: false,
          errMsg: null
        })

        return true;
    }
  )
    .catch (err => this.setState({
      loading: false,
      errMsg: "data unavailable"
    }))


  }

  render() {

    const isThoughtDisplayed = this.state.isThoughtDisplayed;
    let button;
    button = <button onClick={this.getThoughts()}>Display Thought</button>

    if(!isThoughtDisplayed) {
      return( <div>Loading...</div>);
      {button}
    }

    return (
      <div className="thoughtContainer">
        <Thought isThoughtDisplayed={this.state} />
        {button}
      </div>
    );
  }
}

function Thought(state) {
  if (state.isThoughtDisplayed) {
     return <ThoughtData thoughts={state.thoughts}/> ;
  }
  return null;
 }

export default DisplayThoughtButton;
